m:
//settings
POX.setting = {
	name:"sample_basic",
	scale:1.0
};

//make scenedata
const r3=1.0/Math.sqrt(3);
//make primitive model
var sp = new WWModel().primitive("sphere",{div:20,wx:0.5,wy:0.5,wz:0.5})

var scene={
	env:{clear_color:[0.2,0.2,0.4,1.0],cull:false},
	//vertex shader uniform
	vs_uni:{
		col:[1.0,0.2,1.0,1.0]
	},
	//flagment shader uniform
	fs_uni:{
		lvec:[1.0,1.0,1.0],
		mode:0
	},
	model:[
		// model triangle polygon
		{geo:{mode:"tri",
			vtx_at:["position","norm"], // v-shader attribute name
			vtx:[	//VBO buffer
				1,0,0,		//position1
				r3,r3,r3,	//normal1
				0,1,0,		//position2
				r3,r3,r3,	//normal2
				0,0,1,		//position3
				r3,r3,r3	//normal3
			],
			idx:[0,1,2]	//index buffer
			},
			vs_uni:{col:[1.0,0.0,0.0,1.0]}	//v-shader uniform for model
		},
		// model sphere
		{geo:sp.objModel(),
			vs_uni:{col:[0.0,0.0,1.0,1.0]}},
		// axis lines
		{name:"axis",
		geo:{mode:"lines",
			vtx_at:["position"],
			vtx:[0,0,0,200,0,0,0,0,0,0,200,0,0,0,0,0,0,200]},
			fs_uni:{bcolor:[1.0,1.0,1.0,1.0],mode:1}},
	]
}
POX.setScene(scene);

//sceneupdate function for each frame
POX.update=function(render,cam,time){
	var ret={};
	// get first model data
	var m=render.getModelData(0);
	// set modifire matrix
	m.mm=new CanvasMatrix4().rotate(time/50,0,1,0);
	return ret;
}

vs:
attribute vec3 position;
attribute vec3 norm;
attribute vec2 uv ;

uniform mat4 mvpMatrix;
uniform mat4 invMatrix;
uniform vec4 col ;

varying vec3 tnorm ;
varying vec4 color ;

void main() {
	vec2 uv2 = uv ;
	color = col ;
	tnorm = (invMatrix * vec4(norm,0.0)).xyz ;
	gl_Position = mvpMatrix * vec4(position, 1.0) ;
}

fs:
precision highp float;
uniform int mode ;
uniform vec4 bcolor ;
uniform vec3 eyevec ;
uniform vec3 lvec ;	

varying vec3 tnorm ;
varying vec4 color ;

void main() {
	vec3 col = color.xyz ;
	vec3 norm = normalize(tnorm) ;
	float diff= clamp((dot(norm,normalize(lvec))+0.5)/1.5,0.0,1.0);
	float spec= pow(clamp(dot(norm,normalize(normalize(lvec)+normalize(eyevec))),0.0,1.0),60.0);
	vec3 fcolor = col * (diff * 0.8 + 0.2) + vec3(0.6,0.6,1.0)*(spec * 0.8);
	gl_FragColor = (mode!=1) ? vec4(fcolor,color.w):vec4(bcolor.x,bcolor.y,bcolor.z,1.0);
}
