m:
//settings
POX.setting={
	name:"sample_texture",
	scale:1.0
};

//scenedatass
var sp = new WWModel();
sp.primitive("sphere",{div:20});

var scene={
	env:{clear_color:[0.0,0.0,0.0,1.0],cull:false},
	//image texture 
	texture:[
		{src:"sample/tex1024.png"}
	],
	vs_uni:{
	    col:[0.5,0.5,1.0,1.0]
	},
	fs_uni:{
		lvec:[0.6,1.0,0.8],
		tex1:0, //texture index 
		mode:0
	},
	model:[
		{geo:sp.objModel(),},

		{name:"axis",
		geo:{mode:"lines",
			vtx_at:["position"],
			vtx:[0,0,0,200,0,0,0,0,0,0,200,0,0,0,0,0,0,200]},
		fs_uni:{bcolor:[1.0,1.0,1.0,1.0],mode:1}},
	]
}
this.setScene(scene) ;

//sceneupdate
POX.update=function(render,cam,time){
	var ret={};
	var m=render.getModelData(0);
	render.data.model[0].mm = new CanvasMatrix4().rotate(time/30,0,1,0);
	return ret;
}

vs:
attribute vec3 position;
attribute vec3 norm;
attribute vec2 uv ;

uniform mat4 mvpMatrix;
uniform mat4 invMatrix;
uniform vec4 col ;

varying vec3 tnorm ;
varying vec4 color ;
varying vec2 texCoord;

void main() {
	vec2 uv2 = uv ;
	color = col ;
	texCoord	= vec2(uv.x,-uv.y);
	tnorm = (invMatrix * vec4(norm,0.0)).xyz ;
	gl_Position = mvpMatrix * vec4(position, 1.0) ;
}

fs:
precision highp float;
uniform int mode ;
uniform vec4 bcolor ;
uniform vec3 eyevec ;
uniform vec3 lvec ;	
uniform sampler2D tex1;

varying vec3 tnorm ;
varying vec4 color ;
varying vec2 texCoord;

void main() {
	vec3 col = texture2D(tex1, texCoord).xyz ;
	vec3 norm = normalize(tnorm) ;
	float diff= clamp((dot(norm,normalize(lvec))+0.5)/1.5,0.0,1.0);
	float spec= pow(clamp(dot(norm,normalize(normalize(lvec)+normalize(eyevec))),0.0,1.0),20.0);
	vec3 fcolor = col * (diff * 0.8 + 0.2) + vec3(1.0,1.0,1.0)*(spec * 0.6);
	gl_FragColor = (mode!=1) ? vec4(fcolor,color.w):vec4(bcolor.x,bcolor.y,bcolor.z,1.0);
}
