vs:
attribute vec3 position;
attribute vec3 norm;
attribute vec2 uv ;

uniform mat4 mvpMatrix;
uniform mat4 invMatrix;
uniform vec4 col ;

varying vec3 tnorm ;
varying vec4 color ;
varying vec2 texCoord;

void main() {
	vec2 uv2 = uv ;
	color = col ;
	texCoord	= vec2(uv.x,-uv.y);
	tnorm = (invMatrix * vec4(norm,0.0)).xyz ;
	gl_Position = mvpMatrix * vec4(position, 1.0) ;
}

fs:
precision highp float;
uniform int mode ;
uniform vec4 bcolor ;
uniform vec3 eyevec ;
uniform vec3 lvec ;	
uniform sampler2D tex1;

varying vec3 tnorm ;
varying vec4 color ;
varying vec2 texCoord;

void main() {
	vec3 col = texture2D(tex1, texCoord).xyz ;
	vec3 norm = normalize(tnorm) ;
	float diff= clamp((dot(norm,normalize(lvec))+0.5)/1.5,0.0,1.0);
	float spec= pow(clamp(dot(norm,normalize(normalize(lvec)+normalize(eyevec))),0.0,1.0),20.0);
	vec3 fcolor = col * (diff * 0.8 + 0.2) + vec3(1.0,1.0,1.0)*(spec * 0.6);
	gl_FragColor = (mode!=1) ? vec4(fcolor,color.w):vec4(bcolor.x,bcolor.y,bcolor.z,1.0);
}
